-- Creating a table to store ISRO space mission data
CREATE TABLE isro_space_missions (
    mission_id INT PRIMARY KEY,
    mission_name VARCHAR(100),
    launch_date DATE,
    launch_vehicle VARCHAR(50),
    orbit_type VARCHAR(50),
    application VARCHAR(100),
    outcome VARCHAR(50),
    launch_site VARCHAR(100),
    payload VARCHAR(100)
);

